import React, { useEffect, useState } from 'react'
import './Banner.css'
import axios from './Axios';
import requests from './Request'

function Banner() {
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    async function fetchData(){
      const request = await axios.get(requests.fetchNetflixOriginals);
      setMovie(
        request.data.results[Math.floor(Math.random() * request.data.results.length -1)]
      )

    }

  }, [])

  function truncate(string, n){
    if (string?.length > n){
      string = string.substr(0, n-1)+'...';
      return string;

    }else{
      return string;

    }

  }

  return (
    <header className='banner' style = {{
        backgroundSize: "cover",
        backgroundPosition: "center center",
        backgroundImage: `url('https://wallpaperset.com/w/full/9/7/e/437954.jpg')`
    }}>

      <div className="banner__contents">
        <h1 className="banner__title">
          Movie Name
        </h1>
        <div className="banner__buttons">
          <button className="banner__button">
            Play
          </button>
          <button className="banner__button">
            Add to list
          </button>
        </div>
        <h1 className="banner__description">
          {
            truncate (`It is a long established fact that 
            a reader will be distracted by the readable 
            content of a page when looking at its layout. 
            The point of using Lorem Ipsum is that it has a
             more-or-less normal distribution of letters, as
              opposed to using 'Content here, content here', 
              making it look like readable English. Many desktop 
              publishing packages and web page editors now use Lorem
               Ipsum as their default model text, and a search for 
               'lorem ipsum' will uncover many web sites still in 
               their infancy. Various versions have evolved over t
               he years, sometimes by accident, sometimes on purpose 
               (injected humour and the like).`, 150)
          }
        </h1>

      </div>
      <div className="banner--fadingBottom"></div>
    </header>

  )
}

export default Banner
